function showKeys(parent,keys){
   	var tkeys="";
   	for(var subIndex in keys) { 
		if(typeof context.global[parent][subIndex] == "object"){		
			tkeys += " "+ showKeysKeys(subIndex,context.global[parent][subIndex],parent);
		} else {
			tkeys += " "+ parent+"."+subIndex+": "+context.global[parent][subIndex]+", ";	
		}
	}
	return tkeys;
}

function showKeysKeys(parent,keys,pp){
   	var tkeys ="";
   	for(var subIndex in keys) { 
			tkeys += " "+pp+"."+parent+"."+subIndex+": "+context.global[pp][parent][subIndex]+", ";	
	}
	return tkeys;
}

